    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        if(txtNickName.getText().equals("")||txtNickName.getText().equals(" "))
        {
            JOptionPane.showMessageDialog(this, "Please Enter A Valid Client Name: ");
            return;
        }
        new frmMain(txtNickName.getText()).setVisible(true);
        this.dispose();
    }//GEN-LAST:event_jButton1ActionPerformed

